module jt{{.Core}}_game_sdram(
    input           rst,
    input           clk,
`ifdef JTFRAME_CLK24
    input           rst24,
    input           clk24,
`endif
    output          pxl2_cen,   // 12   MHz
    output          pxl_cen,    //  6   MHz
    output   [`JTFRAME_COLORW-1:0]  red,
    output   [`JTFRAME_COLORW-1:0]  green,
    output   [`JTFRAME_COLORW-1:0]  blue,
    output          LHBL,
    output          LVBL,
    output          HS,
    output          VS,
    // cabinet I/O
`ifdef JTFRAME_4PLAYERS
    input    [ 3:0] start_button,
    input    [ 3:0] coin_input,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick1,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick2,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick3,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick4,
`else
    input    [ 1:0] start_button,
    input    [ 1:0] coin_input,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick1,
    input    [`JTFRAME_BUTTONS-1+4:0] joystick2,
`endif
    // SDRAM interface
    input           downloading,
    output          dwnld_busy,

    // Bank 0: allows R/W
    output   [21:0] ba0_addr,
    output   [21:0] ba1_addr,
    output   [21:0] ba2_addr,
    output   [21:0] ba3_addr,
    output   [ 3:0] ba_rd,
    // Write signals
    output   [ 3:0] ba_wr,
    output   [15:0] ba0_din,
    output   [ 1:0] ba0_dsn,  // write mask
    output   [15:0] ba1_din,
    output   [ 1:0] ba1_dsn,
    output   [15:0] ba2_din,
    output   [ 1:0] ba2_dsn,
    output   [15:0] ba3_din,
    output   [ 1:0] ba3_dsn,

    input    [ 3:0] ba_ack,
    input    [ 3:0] ba_dst,
    input    [ 3:0] ba_dok,
    input    [ 3:0] ba_rdy,

    input    [15:0] data_read,
    // ROM LOAD
    input   [24:0]  ioctl_addr,
    input   [ 7:0]  ioctl_dout,
    input           ioctl_wr,
    output  [21:0]  prog_addr,
    output  [15:0]  prog_data,
    output  [ 1:0]  prog_mask,
    output  [ 1:0]  prog_ba,
    output          prog_we,
    output          prog_rd,
    input           prog_ack,
    input           prog_dok,
    input           prog_dst,
    input           prog_rdy,
    // DIP switches
    input   [31:0]  status,     // only bits 31:16 are looked at
    input   [31:0]  dipsw,
    input           service,
    input           dip_pause,
    inout           dip_flip,
    input           dip_test,
    input   [ 1:0]  dip_fxlevel,
    // Sound output
    output  signed [15:0] snd,
    output          sample,
    output          game_led,
    input           enable_psg,
    input           enable_fm,
    // Debug
`ifdef JTFRAME_DEBUG
    input   [ 7:0]  debug_bus,
    output  [ 7:0]  debug_view,
`endif
    input   [ 3:0]  gfx_en
);

