    .prog_data    ( header ? ioctl_dout       : prog_data[7:0] ),
    .prog_we      ( header ? ioctl_wr         : prog_we        ),
    .prom_we      ( prom_we        ),
`endif
    .header       ( header         ),
    // Debug  
`ifdef JTFRAME_DEBUG
    .debug_bus    ( debug_bus      ),
    .debug_view   ( debug_view     ),
`endif
